package aula03_04;

public class Ex01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//soma e concatenação
		int n1 = 2;
		int n2 = 4;
		int n3 = 2;
		int n4 = 4;

		System.out.println("A soma de " + n1 + " + " + n2 + " + " + n3 + " + " + n4 + " = " + (n1+n2+n3+n4));
		System.out.println("A subtração de " + n1 + " - " + n2 + " - " + n3 + " - " + n4 + " = " + (n1-n2-n3-n4));
		System.out.println("A multiplicação de " + n1 + " * " + n2 + " * " + n3 + " * " + n4 + " = " + (n1*n2*n3*n4));
	}

}
